import { useState } from 'react'
import {Link} from "react-router-dom"
import {v4} from 'uuid'
import { FiUser } from "react-icons/fi";
import { FiMail } from "react-icons/fi";
import { BsFillCalendar2DateFill } from "react-icons/bs";
import './index.css'

function getData() {
  let stringifiedData = localStorage.getItem("userlist");
  let parsedData = JSON.parse(stringifiedData);
  if (parsedData === null) {
      return [];
  } else {
      return parsedData;
  }
}

let initialList = getData();


const CreateUser=(props)=>{
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [dobInput, setDOB] = useState("")
  const [alertMsg,setAlert]=useState(false)

  const onChangeDOBInput = event => {
    setDOB(event.target.value)
  }

  const onChangeNameInput = event => {
    setName(event.target.value)
    //var alertName=nameInput.match(/^[A-Za-z ]+$/)?true:false;
  }

  const onChangeEmailInput = event => {
    setEmail(event.target.value)
  }

  const onAddItem = event => {
    event.preventDefault()
    const newItem = {
      id: v4(),
      name: nameInput,
      email: emailInput,
      dob: dobInput,
    }

    if (nameInput==='' || emailInput==='' || dobInput==='' ){
      setAlert(true)
    }else{
    initialList.push(newItem)
    localStorage.setItem('userlist',JSON.stringify(initialList))
    setName("")
    setEmail("")
    setDOB("")
    setAlert(false)
    const {history}=props
    history.replace('/')
  }
  }
    return (
      <div className="form-container">
        <div className="login-form">
         <form onSubmit={onAddItem}>
            <label htmlFor="name" className="label">Name</label>
            <div className="container">
               <div className="icon-container"><FiUser className="icon-size" /></div>
               <input
                type="text"
                id="name"
                value={nameInput}
                onChange={onChangeNameInput}
                className="input"
                placeholder="Name"
              />
            </div>
            <label htmlFor="email" className="label">Email</label>
            <div className="container">
              <div className="icon-container"><FiMail className="icon-size" /></div>
              <input
                type="text"
                id="email"
                email={emailInput}
                onChange={onChangeEmailInput}
                className="input"
                placeholder="Email"
              />
            </div>
            <label htmlFor="date" className="label">DOB</label>
            <div className="container">
              <div className="icon-container"><BsFillCalendar2DateFill className="icon-size" /></div>
              <input
                type="text"
                id="date"
                value={dobInput}
                onChange={onChangeDOBInput}
                className="input"
              />
            </div>
            <button type="submit" className="add-button"><Link to='/'>Add</Link></button>
         </form>
         {alertMsg && <p className="err-msg">Required all fields</p>}
         </div>
      </div>
    )
  }


export default CreateUser
