import {useState} from 'react'
import {v4} from 'uuid'
import UserDetails from '../UserDetails'

import './index.css'

const CreateUser = (props) => {
    const [userlist,setList]=useState([])
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [dob,setDob]=useState("")
    const [blurName,setBlurName]=useState(false)
    const [blurEmail,setBlurEmail]=useState(false)
    const [blurDOB,setBlurDOB]=useState(false)
    const [alertName,setAlertName]=useState(false)
    const [alertEmail,setAlertEmail]=useState(false)
    const [alertDOB,setAlertDOB]=useState(false)
    const [alertMsg,setAlertMsg]=useState(false)

    const onSubmitForm = (event) =>{
        event.preventDefault();
        const newFormData = {
            id:v4(),
            name:name,
            email:email,
            dob:dob
        }
        if ( name==="" || email==="" || dob==="") {
            setAlertMsg(true)
        }else{
          if (userlist.length===0){
            setList([newFormData])   
            console
          }else{
              const val=userlist.find(each=>each.email===newFormData.email)
              if (val){
                   setAlertEmail(true)
              }else{
                let updatedList=[...userlist,newFormData]
                setList(updatedList)
                console.log(userlist);
                <UserDetails userData={userlist} key={userlist.id}/>
            }
          }  
        } 
    }

    const onBlurName=(event)=>{
      if(event.target.value===""){
        setBlurName(true)
      }else{
        setBlurName(false)
      }
    }
    const onBlurEmail=(event)=>{
      if(event.target.value===""){
        setBlurEmail(true)
      }else{
        setBlurEmail(false)
      }
    }
    const onBlurDOB=(event)=>{
      if(event.target.value===""){
        setBlurDOB(true)
      }else{
        setBlurDOB(false)
      }
    }

    const onChangeName=(event)=>{
      setName(event.target.value)
      let validName=name.match(/^[A-Za-z ]+$/)
      if(!validName){
        setAlertName(true)
        setBlurName(false)
      }else{
        setAlertName(false)
      } 
    }

    const onChangeEmail = (event) =>{
        setEmail(event.target.value)
        var validEmail =email.match( /^((([a-zA-Z]|[0-9])|([-]|[_]|[.])){1,})+[@](([a-zA-Z0-9])|([-]|[.])){2,40}[.]((([a-zA-Z0-9]){2,10})|(([a-zA-Z0-9]){2,4}[.]([a-zA-Z0-9]){2,4}))$/);
        if (!validEmail){
          setAlertEmail(true)
          setBlurEmail(false)
        }else{
            setAlertEmail(true)
        }
    }

    const onChangeDate = (event) =>{
        if (event.target.value!==""){
            setDob(event.target.value)
        }
        else{
            setAlertDOB(true)
        }
    }


    return(

            <div className="main-container">
                <div className = "sub-container">
                <div className="form-container">
                    <form className="form" onSubmit={onSubmitForm}>
                        <h1 className="heading">DATA FORM</h1>
                        <div className="input-container">
                            <label className="input-label" htmlFor='name'>NAME </label>
                            <input className='input-field' value={name} onBlur={onBlurName} onChange={onChangeName} placeholder="Enter your name" id='name'/>
                            {blurName && <p className='error-msg'>**Required**</p>}
                            {alertName && <p className='error-msg'>Enter Valid Name</p>}
                        </div>
                        <div className="input-container">
                            <label className="input-label" htmlFor='email'>EMAIL </label>
                            <input className='input-field' value={email} onBlur={onBlurEmail} onChange={onChangeEmail} placeholder="Enter your email" id='email'/>
                            {blurEmail && <p className='error-msg'>**Required**</p>}
                            {alertEmail && <p className='error-msg'>Enter Valid Email</p>}
                        </div>
                        <div className="input-container">
                            <label className="input-label" htmlFor='dob'>DATE OF BIRTH</label>
                            <input className='input-field' value={dob} onBlur={onBlurDOB} onChange={onChangeDate}  id='dob' type="date"/>
                            {blurDOB && <p className='error-msg'>**Required**</p>}
                            {alertDOB && <p className='error-msg'>Enter Valid DOB</p>}
                        </div>
                        {alertMsg && <p className='error-msg'>Enter all details</p>}
                        <button type="submit" className='button'>Submit</button>
                    </form>
                </div>
                </div>
            </div>

    )

}

export default CreateUser
