import {useState} from 'react'
import {v4} from 'uuid'

import './index.css'



const CreateUser = (props) => {
    let [name,setName]=useState("")
    let [email,setEmail]=useState("")
    let [dob,setDob]=useState("")
    let [blurMsg,setBlur]=useState(false)
    let [alertName,setAlertName]=useState(false)
    let [alertEmail,setAlertEmail]=useState(false)
    let [alertDOB,setAlertDOB]=useState(false)
    let [alertMsg,setAlertMsg]=useState(false)

    const onSubmitForm = (event) =>{
        event.preventDefault();
        const newFormData = {
            id:v4(),
            name:name,
            email:email,
            dob:dob
        }

        if ( name==="" || email==="" || dob==="") {
            setAlertMsg(alertMsg=true)
        }else{
          const getData = localStorage.getItem("userData")
          const parsedGetData = JSON.parse(getData)
          const val=parsedGetData.find(each=>each.email===newFormData.email)
          if (val){
            setAlertEmail(alertEmail=true)
          }else{
            if (parsedGetData === null){
                const newData = [newFormData]
                localStorage.setItem("userData",JSON.stringify(newData))
            }
            else{
                const newData = [...parsedGetData,newFormData]
                localStorage.setItem("userData",JSON.stringify(newData))
            }
          }  
        }
        const {history} = props 
        history.replace('/')

    }

    const blurName=(event)=>{
      if(event.target.value===""){
        setAlertName(true)
      }
    }
    const blurEmail=(event)=>{
      if(event.target.value===""){
        setAlertEmail(true)
      }
    }
    const blurDOB=(event)=>{
      if(event.target.value===""){
        setAlertDOB(true)
      }else{
        set
      }
    }

    const onChangeName=(event)=>{
        if (event.target.value!==""){
            setName(event.target.value)
            let val=name.match(/^[A-Za-z ]+$/)
            if(val){
              setAlertName(alertName=true)
          }else{
            setAlertName(alertName=false)
          }
        }
        else{
            setAlertName(alertName=true)
        }

    }

    const onChangeEmail = (event) =>{
        if (event.target.value!==""){
            setEmail(event.target.value)
        }
        else{
            setAlertEmail(alertEmail=true)
        }
    }

    const onChangeDate = (event) =>{
        if (event.target.value!==""){
            setDob(event.target.value)
        }
        else{
            setAlertDOB(alertDOB=true)
        }
    }


    return(

            <div className="main-container">
                <div className = "sub-container">
                <div className="form-container">
                    <form className="form" onSubmit={onSubmitForm}>
                        <h1 className="heading">DATA FORM</h1>
                        <div className="input-container">
                            <label className="input-label" htmlFor='name'>NAME </label>
                            <input className='input-field' value={name} onBlur={blurName} onChange={onChangeName} placeholder="Enter your name" id='name'/>
                            {alertName && <p className='error-msg'>Enter Valid Name</p>}
                        </div>
                        <div className="input-container">
                            <label className="input-label" htmlFor='email'>EMAIL </label>
                            <input className='input-field' value={email} onBlur={blurEmail} onChange={onChangeEmail} placeholder="Enter your email" id='email'/>
                            {alertEmail && <p className='error-msg'>Enter Valid Email</p>}
                        </div>
                        <div className="input-container">
                            <label className="input-label" htmlFor='dob'>DATE OF BIRTH</label>
                            <input className='input-field' value={dob} onBlur={blurDOB} onChange={onChangeDate}  id='dob' type="date"/>
                            {alertDOB && <p className='error-msg'>Enter Valid DOB</p>}
                        </div>
                        {alertMsg && <p className='error-msg'>Enter all details</p>}
                        <button type="submit" className='button'>Submit</button>
                    </form>
                </div>
                </div>
            </div>

    )

}

export default CreateUser
