import DataContext from "../Context";
import {v4} from "uuid";
import React, { useState } from 'react';
import { FaUserAlt } from "react-icons/fa";
import {AiFillMail} from "react-icons/ai";
import "./index.css"

const CreateUser=(props)=> {


    let [blurDob, setblurDob] = useState(false);
    let [name,setName]=useState("")
    let [email,setEmail]= useState("")
    let [dob,setDob]= useState("")
    let [ValidName,setNameDetails]=useState({ValidName:''})
    let [ValidEmail,setEmailDetails]=useState({ValidEmail:''})
    let [ValidDob,setDobDetails]=useState({ValidDob:''})



    const nameInput=(event)=> {
        setName(event.target.value)
        if ((event.target.value).match(/^[A-Za-z ]+$/)){
          setNameDetails({ValidName:''})
        }else{
          setNameDetails({ValidName:'Invalid Name'})
        }
        if (event.target.value===""){
          setNameDetails({ValidName:"Required"})
        }
     }
     const emailInput=(event)=>{
        setEmail(event.target.value)
        var regex = /^((([a-zA-Z]|[0-9])|([-]|[_]|[.])){1,})+[@](([a-zA-Z0-9])|([-]|[.])){2,40}[.]((([a-zA-Z0-9]){2,10})|(([a-zA-Z0-9]){2,4}[.]([a-zA-Z0-9]){2,4}))$/;
        if (regex.test(event.target.value)){
            setEmailDetails({ValidEmail:""})
        }else{
          setEmailDetails({ValidEmail:"Invalid E-mail"})
        }
        if (event.target.value===""){
          setEmailDetails({ValidEmail:"Required"})
        }
    }
     const dateInput=(event)=>{
         const userDateBirth=new Date(event.target.value)
         const nowDate=new Date()
         if(event.target.value===""){
             console.log("date")
         }else{
            if(userDateBirth.toString() ==="Invalid Date"){
                setErrorDate(true)
            }else{
                setErrorDate(errorDate=false)
                const diff=nowDate.getFullYear()-userDateBirth.getFullYear()
                setdate(diff)

            }
        }
        }


    return <DataContext.Consumer>
        {value=>{
          const {list,addList}=value
          const submtDetails=(event)=>{
                  event.preventDefault()
                  const newList={
                    id:v4(),
                    name:name,
                    email:email,
                    date:date,
                  }
            if (name===""){
                setNameDetails({ValidName:"Required"})
            }
            if (email===""){
                setEmailDetails({ValidEmail:"Required"})
            }
            if (dob===""){
                setDobDetails({ValidEmail:"Required"})
            }
            if(NameDetails.Name==="" || email==="" || date===""){
                //setblurName(true);
                setErrorEmail(false)
                seteamilChecking(false)
            }else {
              const emailAvalibel=list.find(each=>each.email===newList.email)
              if(emailAvalibel){
                seteamilChecking(true)
                setErrorEmail(false)  
              }else{
                seteamilChecking(false)
                addList(newList)
                const {history}=props 
                history.replace('/')
              }   
            }
        }
        return (
        <div className="bg-container">
            <form className="form" onSubmit={submtDetails}>
                <label htmlFor="text">Name</label>
                <div className="margin">
                    <div className="input-container" id="text">
                        <div className="icon-container">
                            <FaUserAlt className="image" />
                        </div>
                        <input type="text" id="text" value={NameDetails.Name} className="inputText" onChange={nameInput}/> 
                    </div>
                    <p className="errorMsg">{NameDetails.ValidName}{NameDetails.BlurName}</p>

                    <br/>
                </div>
                <label htmlFor="email">Email</label>
                <div className="margin">
                    <div className="input-container" id="email">
                        <div className="icon-container">
                            <AiFillMail className="image" />
                        </div>
                        <input type="text" id="text" className="inputText" onChange={emailInput} onBlur={blurEmailInput} />
                    </div>
                    {blurEmail ? <p className="errorMsg">**required**</p> : <p></p>}
                    {errorEmail &&<p className="errorMsg">Email worng</p>}
                    {eamilChecking &&<p className="errorMsg">Email alredy exits</p>}
                    <br/>
                </div>
                <label htmlFor="date">Date Of Birth: MM/DD/YY</label>
                <div className="margin">
                    <div className="input-container" id="date">
                        <div className="icon-container">
                            <AiFillMail className="image" />
                        </div>
                        <input type="text" id="text" className="inputText" onChange={dateInput} onBlur={blurDateInput} />
                    </div>
                    {blurDob ? <p className="errorMsg">**required**</p> : <p></p>}
                    {errorDate? <p className="errorMsg">Date formate is worng</p>:<p></p>}
                    <br/>
                </div>

                <button type="submit" className="btn">Submit</button>
            </form>
    </div>)
        }
    }
        </DataContext.Consumer>


}

export default CreateUser
