import {useState} from 'react'
import {v4} from 'uuid'

import './index.css'



const CreateUser = (props) => {
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [dob,setDob]=useState("")
    const [alertName,setAlertName]=useState(false)
    const [alertEmail,setAlertEmail]=useState(false)
    const [alertDOB,setAlertDOB]=useState(false)
    const [alertMsg,setAlertMsg]=useState(false)

    const onSubmitForm = (event) =>{
        event.preventDefault();
        const newFormData = {
            id:v4(),
            name:name,
            email:email,
            dob:dob
        }

        if ( name==="" || email==="" || dob==="") {
            setAlertMsg(true)
        }else if(name.match(/^[A-Za-z ]+$/)){
            setAlertName(true)
            alert("Please Enter Validname")
        }
        else if( email===""){
            setAlertEmail(true)
            alert("Please Enter email")
        }
        else if( dob===""){
            setAlertDOB(true)
            alert("Please Enter dob")
        }else{
            const getData = localStorage.getItem("userData")
            const parsedGetData = JSON.parse(getData)
            if (parsedGetData === null){
                const newData = [newFormData]
                localStorage.setItem("userData",JSON.stringify(newData))
            }
            else{
                const newData = [...parsedGetData,newFormData]
                localStorage.setItem("userData",JSON.stringify(newData))
            }

        }
        const {history} = props 
        history.replace('/')

    }


    function onChangeName(event){
        if (event.target.value!==""){
            setName(event.target.value)
        }
        else{
            setName("")
        }

    }

    const onChangeEmail = (event) =>{
        if (event.target.value!==""){
            setEmail(event.target.value)
        }
        else{
            setEmail("")
        }
    }

    const onChangeDate = (event) =>{
        if (event.target.value!==""){
            setDob(event.target.value)
        }
        else{
            setDob("")
        }
    }


    return(

            <div className="main-container">
                <div className = "sub-container">
                <div className="form-container">
                    <form className="form" onSubmit={onSubmitForm}>
                        <h1 className="heading">DATA FORM</h1>
                        <div className="input-container">
                            <label className="input-label" htmlFor='name'>NAME </label>
                            <input className='input-field' value={name} onChange={onChangeName} placeholder="Enter your name" id='name'/>
                            {alertName && <p className='error-msg'>Enter Valid Name</p>}
                        </div>
                        <div className="input-container">
                            <label className="input-label" htmlFor='email'>EMAIL </label>
                            <input className='input-field' value={email} onChange={onChangeEmail} placeholder="Enter your email" id='email'/>
                            {alertEmail && <p className='error-msg'>Enter Valid Email</p>}
                        </div>
                        <div className="input-container">
                            <label className="input-label" htmlFor='dob'>DATE OF BIRTH</label>
                            <input className='input-field' value={dob} onChange={onChangeDate}  id='dob' type="date"/>
                            {alertDOB && <p className='error-msg'>Enter Valid DOB</p>}
                        </div>

                        <button type="submit" className='button'>Submit</button>
                    </form>
                </div>
                </div>
            </div>

    )

}

export default CreateUser
