const initialState2 = "";
const appReducer2 = (state = initialState2, action) => {
	switch (action.type) {
		case "greeting":
			setInterval(() => {
				state = `Welcome ${action.name}`;
			}, 1000);
			return state;
		default:
			return state;
	}
	//reducer
};

export default appReducer2;
