const initialState2 = "";
const appReducer2 = (state = initialState2, action) => {
	switch (action.type) {
		case "greeting":
			if (state === "") {
				const msg = setInterval(() => {
					console.log(`Welcome ${action.name}`);
				}, 1000);
			} else {
				clearInterval(msg);
			}

			return (state = "");

		default:
			return state;
	}
	//reducer
};

export default appReducer2;
