import React, {useState} from "react"
import './App.css';
import Header from './components/header';
import Footer from './components/footer';
import Form from './components/form';
function App() {
  const[inputValue, setInputValue] = useState("")
  const [showDialog,setShowDialog] = useState(false);

  function handleDialog(e){
    e.preventDefault()
    setShowDialog(true)
  }
  function clearDialogs(e){
    e.preventDefault()
    setInputValue("")
  } 

  return (
    <div className="App">
      <div className="App-header">
        <Header />
        <Form/>
        <div className="bgContainer">
          <form {...clearDialogs}>
            <input className="inp" onChange={(e) => setInputValue(e.target.value)} type="text" placeholder="Enter any text" />
            <div className="btn" >
              <button type="submit" onClick={handleDialog} className="btnStyle" >Click me</button>
              <button type="re" onClick={clearDialogs} className="btnStyle" >Reset</button>
            </div>

            {showDialog && <div>{inputValue}</div>}
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
