import React, { useState } from 'react';
import PopupCntr from '../popup/components/popupCntr.js';
import CardDitails from '../popup/components/cardDitails'
import ProductDetails from '../popup/components/productDetails.js';
import Imgcntr from './components/imgcntr.js';
function App() {
  const [isOpen, setIsOpen] = useState(false);

  const togglePopup = () => {
    setIsOpen(!isOpen);
  }

  return <>
    <input
      type="button"
      value="View More Details"
      className="ViewMoreBtn  textCenter"
      onClick={togglePopup}
    />

    {isOpen && <PopupCntr
      content={
      <>
      <div className='CDCntr'>
                    <div className='rowFlex'>
                        <div className='leftBTNPart'>
                            <span className='CDTxt'>Heat Pump Electric Water Heater {">"}  Rheem Perfomance platinum 50</span>
                        </div>
                        <div className='rightBTNPart'>
                        <i class="fas fa-angle-double-left preicon"></i> <button className='pdngSM previous'>  Previous</button>
                            <button className='pdngSM next' > Next </button><i class="fas fa-angle-double-right preicon"></i>
                        </div>
                    </div>
                </div>
      <div className='rowFlex'>
      <div className='aside'>
        <Imgcntr/>
        </div>
      <div className='colFlex main'>
      <CardDitails />
      <ProductDetails />
      </div>
      </div>



      </>
      }handleClose={togglePopup}
    />}
  </>
}

export default App;
